const inputs=document.querySelectorAll(".inputClass"),registerLinks=document.querySelectorAll(".register"),screenPrava=document.querySelector(".screen-prava"),screenLeva=document.querySelector(".screen-leva"),clouds=document.querySelector(".mobile-clouds");for(const a of inputs)a.addEventListener("input",function(){""!=a.value?a.nextElementSibling.classList.add("offset"):a.nextElementSibling.classList.remove("offset")});for(const b of registerLinks)b.addEventListener("click",function(){screenPrava.classList.toggle("switchRegistrationWhite"),screenLeva.classList.toggle("switchRegistrationColor"),clouds.classList.toggle("registerCloud")});