const inputs=document.querySelectorAll(".inputClass"),registerLinks=document.querySelectorAll(".register"),screenPrava=document.querySelector(".screen-prava"),screenLeva=document.querySelector(".screen-leva"),clouds=document.querySelector(".mobile-clouds"),openMenuBtn=document.querySelector("#openMenuBtn"),closeMenuBtn=document.querySelector("#closeMenuBtn"),overlay=document.querySelector(".overlay"),blurBg=document.querySelector(".blur"),mainPage=document.querySelector(".mainPage");for(const a of inputs)a.addEventListener("input",function(){""!=a.value?a.nextElementSibling.classList.add("offset"):a.nextElementSibling.classList.remove("offset")});for(const b of registerLinks)b.addEventListener("click",function(){screenPrava.classList.toggle("switchRegistrationWhite"),screenLeva.classList.toggle("switchRegistrationColor"),clouds.classList.toggle("registerCloud")});openMenuBtn.addEventListener("click",function(){overlay.style.height="45%",blurBg.style.height="100%"}),closeMenuBtn.addEventListener("click",function(){overlay.style.height="0%",blurBg.style.height="0%"});